import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import Video from "../../videos/PMstartMovie.mp4";
import { Button } from "../ButtonElements";
import { NavLinks } from "../Navbar/NavbarElements";
import {
  HeroContainer,
  HeroBg,
  VideoBg,
  HeroContent,
  HeroH1,
  HeroP,
  HeroBtnWrapper,
  ArrowForward,
  ArrowRight,
} from "./HeroElements";

export const HeroSection = () => {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover)
  }

  return (
    <HeroContainer id="start">
      <HeroBg>
        <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
      </HeroBg>
      <HeroContent>
        <HeroH1>POGOTOWIE MUZYCZNE</HeroH1>
        <HeroP>Zadbaj o świetną zabawę na swoim weselu razem z nami!</HeroP>
        <HeroBtnWrapper>
          <NavLinks
            to="offer"
            smooth={true}
            duration={500}
            spy={true}
            exact="true"
            offset={-80}>
            <Button onMouseEnter={onHover} onMouseLeave={onHover}>
              Przejdź do oferty! {hover ? <ArrowForward /> : <ArrowRight />}
            </Button>
          </NavLinks>
        </HeroBtnWrapper>
      </HeroContent>
    </HeroContainer>
  );
};
