import styled from 'styled-components'
import { MdKeyboardArrowRight, MdArrowForward, FaAngleDown, FaAngleDoubleDown } from 'react-icons/fa';
import { Link as LinkS } from 'react-scroll'

export const NavLinks = styled(LinkS)`
    color: #000;
    display: flex;
    justify-content: center;
    align-items: center;
    text-decoration: none;
    padding: 1rem;
    margin-top: 32px;

    //width: 320px;;

    cursor: pointer;
    background: #01bf71;
    border-radius: 25px;

    &:hover {
        // background: #01bf71;
        transition: all 0.2s ease-in-out;
        // font-size: 1.05rem;
        background: #fff
    }

`;

export const ArrowForward = styled(FaAngleDoubleDown)`
  margin-left: 8px;
  font-size: 20px;
`;

export const ArrowRight = styled(FaAngleDown)`
  margin-left: 8px;
  font-size: 20px;
`;
