import styled from 'styled-components'
import { Link as LinkS } from 'react-scroll'

export const NavLinks = styled(LinkS)`
    color: #000;
    display: flex;
    align-items: center;
    text-decoration: none;
    padding: 1rem;

    height: 100%;
    cursor: pointer;
    background: #01bf71;
    border-radius: 25px;


    &:hover {
        // background: #01bf71;
        font-size: 1.05rem;
        background: rgba(1, 191, 113, .9)
    }
`;
